def argmax(l):
    f = lambda i: l[i]
    return max(range(len(l)), key=f)
# Classifier

def single_classify(row, return_probabilities=False):
    #inits
    x = row
    o = [0] * num_output_logits


    #Nueron Equations
    h_0 = max((((0.041223254 * float(x[0]))+ (-21.882261 * float(x[1]))+ (-0.5203875 * float(x[2]))+ (-1.3993442 * float(x[3]))+ (0.1896159 * float(x[4]))+ (-0.2394098 * float(x[5]))+ (-22.88092 * float(x[6]))+ (-2.784454 * float(x[7]))+ (0.18710935 * float(x[8]))+ (-0.3263968 * float(x[9]))) + -0.11799386), 0)
    h_1 = max((((3.1185663 * float(x[0]))+ (0.0004187199 * float(x[1]))+ (10.020925 * float(x[2]))+ (0.063685656 * float(x[3]))+ (1.0805659 * float(x[4]))+ (-0.050329056 * float(x[5]))+ (0.0021272295 * float(x[6]))+ (-0.00079280545 * float(x[7]))+ (-0.03866949 * float(x[8]))+ (-0.43421182 * float(x[9]))) + -0.17753825), 0)
    o[0] = (-0.8556564 * h_0)+ (-0.25706515 * h_1) + 3.0419831



    #Output Decision Rule
    if num_output_logits==1:
        if return_probabilities:
            exp_o = 1./(1. + np.exp(-o[0]))
            return np.array([exp_o, 1.-exp_o])
        else:
            return o[0]>=0
    else:
        if return_probabilities:
            exps = np.exp(o)
            Z = sum(exps).reshape(-1, 1)
            return exps/Z
        else:
            return argmax(o)


def classify(arr, transform_true=False, return_probabilities=False):
    #apply transformation if necessary
    if transform_true:
        trans = transform(arr[:, :-1])
        arr = np.concatenate((trans, cleanarr[:, -1].reshape(-1, 1)), axis = 1)
    #init
    w_h = np.array([[0.041223254054784775, -21.882261276245117, -0.5203874707221985, -1.3993442058563232, 0.1896159052848816, -0.23940980434417725, -22.88092041015625, -2.784454107284546, 0.1871093511581421, -0.32639679312705994], [3.1185662746429443, 0.0004187198937870562, 10.02092456817627, 0.06368565559387207, 1.0805659294128418, -0.0503290556371212, 0.002127229468896985, -0.0007928054546937346, -0.038669489324092865, -0.4342118203639984]])
    b_h = np.array([-0.11799386143684387, -0.17753824591636658])
    w_o = np.array([[-0.8556563854217529, -0.25706514716148376]])
    b_o = np.array(3.041983127593994)

    #Hidden Layer
    h = np.dot(arr, w_h.T) + b_h
    
    relu = np.maximum(h, np.zeros_like(h))


    #Output
    out = np.dot(relu, w_o.T) + b_o
    if return_probabilities:
        return np.array(np.exp(out)) / np.sum(np.exp(out), axis=1).reshape(-1,1)
    if num_output_logits == 1:
        if return_probabilities:
            exp_o = 1./(1. + np.exp(-out))
            return np.concatenate((exp_o, 1.-exp_o), axis=1)
        else:
            return (out >= 0).astype('int').reshape(-1)
    else:
        if return_probabilities:
            exps = np.exp(out)
            Z = np.sum(exps, axis=1).reshape(-1, 1)
            return exps/Z
        else:
            return (np.argmax(out, axis=1)).reshape(-1)



def Predict(arr,headerless,csvfile, get_key, classmapping):
    with open(csvfile, 'r', encoding='utf-8') as csvinput:
        #readers and writers
        reader = csv.reader(csvinput)

        #print original header
        if (not headerless):
            print(','.join(next(reader, None) + ["Prediction"]))
        
        outputs = classify(arr)
        for i, row in enumerate(reader):
            #use the transformed array as input to predictor
            pred = str(get_key(int(outputs[i]), classmapping))
            #use original untransformed line to write out
            row.append(pred)
            print(','.join(['"' + field + '"' if ',' in field else field for field in row]))


def Validate(cleanarr):
    if n_classes == 2:
        #note that classification is a single line of code
        outputs = classify(cleanarr[:, :-1])


        #metrics
        count, correct_count, num_TP, num_TN, num_FP, num_FN, num_class_1, num_class_0 = 0, 0, 0, 0, 0, 0, 0, 0
        correct_count = int(np.sum(outputs.reshape(-1) == cleanarr[:, -1].reshape(-1)))
        count = outputs.shape[0]
        num_TP = int(np.sum(np.logical_and(outputs.reshape(-1) == 1, cleanarr[:, -1].reshape(-1) == 1)))
        num_TN = int(np.sum(np.logical_and(outputs.reshape(-1) == 0, cleanarr[:, -1].reshape(-1) == 0)))
        num_FN = int(np.sum(np.logical_and(outputs.reshape(-1) == 0, cleanarr[:, -1].reshape(-1) == 1)))
        num_FP = int(np.sum(np.logical_and(outputs.reshape(-1) == 1, cleanarr[:, -1].reshape(-1) == 0)))
        num_class_0 = int(np.sum(cleanarr[:, -1].reshape(-1) == 0))
        num_class_1 = int(np.sum(cleanarr[:, -1].reshape(-1) == 1))
        return count, correct_count, num_TP, num_TN, num_FP, num_FN, num_class_1, num_class_0, outputs


    else:
        #validation
        outputs = classify(cleanarr[:, :-1])


        #metrics
        count, correct_count = 0, 0
        numeachclass = {}
        for k, o in enumerate(outputs):
            if int(o) == int(float(cleanarr[k, -1])):
                correct_count += 1
            if int(float(cleanarr[k, -1])) in numeachclass.keys():
                numeachclass[int(float(cleanarr[k, -1]))] += 1
            else:
                numeachclass[int(float(cleanarr[k, -1]))] = 1
            count += 1
        return count, correct_count, numeachclass, outputs
    


